mapper.select:
SELECT SEQ, CATEGORY, ID, TITLE, CONTENT, REF, STEP, DEPT, WDATE, DEL
FROM QNA
WHERE ID=#{id}


SELECT SEQ, ID, IMAGENAME, TITLE, CATEGORY, BRAND, PRICE, OPTIONS, CONTENT, RENTALCOUNT, WDATE 
FROM GOODS
WHERE ID=#{id}

SELECT SEQ, ID, IMAGENAME, TITLE, CATEGORY, BRAND, PRICE, OPTIONS, CONTENT, RENTALCOUNT, WDATE 
FROM GOODS 
WHERE SEQ=#{seq}
